import React, { Component } from "react";
import "./App.css";
import UploadPhoto from "./images/upload_file.png";

class App extends Component {
  state = {
    image: UploadPhoto
  };
  //function for fileUpload
  onChange = e => {
    let files = e.target.files;
    let reader = new FileReader();
    if (files.length > 0) {
      reader.readAsDataURL(files[0]);
    }
    console.log(this);

    reader.onload = e => {
      const imageDataLoad = e.target.result;
      this.setState({ image: imageDataLoad });
      console.log(
        document.querySelectorAll("div[imageHidden='true']").length > 0
          ? document
              .querySelectorAll("div[imageHidden='true']")[0]
              .removeAttribute("imageHidden")
          : "false"
      );
      /* document.querySelectorAll("div[imageHidden='true']").length > 0
        ? (document.querySelectorAll("div[imageHidden='true']")[0].removeAttribute("imageHidden"),)
        : null; */
      document.getElementById("file-upload").setAttribute("visible", "false");
    };
  };

  render() {
    return (
      <div className="main-container">
        <div className="header" id="item-1">
          <h1>Simple Inventory System</h1>
        </div>
        <div className="items-list">
          <div>
            <div>#</div>
            <div>Product Name</div>
            <div>Quantity</div>
            <div>Measurement</div>
            <div>Original Price</div>
            <div>Profit</div>
            <div>Selling Price</div>
            <div>Supplier</div>
            <div>Delete</div>
            <div>Edit</div>
          </div>
          {/* <FileUpload isChange={this.onChange} imageData={this.state.image} /> */}
        </div>
      </div>
    );
  }
}

export default App;
