import React, { Component } from "react";
import "./App.css";
import FileUpload from "./components/fileUpload";
import UploadPhoto from "./images/upload_file.png";

class App extends Component {
  state = {
    image: UploadPhoto
  };
  //function for fileUpload
  onChange = e => {
    let files = e.target.files;
    let reader = new FileReader();
    if (files.length > 0) {
      reader.readAsDataURL(files[0]);
    }
    console.log(this);

    reader.onload = e => {
      const imageDataLoad = e.target.result;
      this.setState({ image: imageDataLoad });
      console.log(
        document.querySelectorAll("div[imageHidden='true']").length > 0
          ? "true"
          : "false"
      );
      /* document.querySelectorAll("div[imageHidden='true']").length > 0
        ? (document.querySelectorAll("div[imageHidden='true']")[0].removeAttribute("imageHidden"),)
        : null; */
      document.getElementById("file-upload").setAttribute("visible", "false");
    };
  };

  render() {
    return (
      <div className="main-container">
        <div className="header" id="item-1">
          <h1>Simple Casher Counter</h1>
        </div>
        <div className="casher-contents">
          <div className="casher-buttons">
            <div>
              <button>7</button>
              <button>8</button>
              <button>9</button>
            </div>
            <div>
              <button>4</button>
              <button>5</button>
              <button>6</button>
            </div>
            <div>
              <button>1</button>
              <button>2</button>
              <button>3</button>
            </div>
            <div>
              <button>0</button>
              <button className="enter-button">Enter</button>
            </div>
          </div>
          <div className="items-list">
            <h2>Items List</h2>
            <FileUpload isChange={this.onChange} imageData={this.state.image} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
