import React, { Component } from "react";
import "./App.css";
import Items from "./components/items";
import AddProduct from "./components/addproduct";

class App extends Component {
  state = {
    window_value: "false",
    items: [
      {
        id: 1,
        product_name: "Pen",
        quantity: 35,
        measurement: "Piece",
        original_price: 25,
        profit: 10,
        selling_price: 35,
        supplier: "Smart Supplier"
      }
    ],
    product_form_data: {
      product_name: "",
      quantity: 0,
      measurement: "",
      original_price: 0,
      profit: 0,
      selling_price: 0,
      supplier: ""
    },
    test: {
      test1: function(e) {
        this.onUpdateProductName(e);
      }
    }
  };

  onUpdateProductName = e => {
    this.setState({ product_form_data: { product_name: e.target.value } });
  };
  onUpdateQuantity = e => {
    this.setState({ product_form_data: { quantity: e.target.value } });
  };
  onUpdateMeasurement = e => {
    this.setState({ product_form_data: { measurement: e.target.value } });
  };
  onUpdateOriginalPrice = e => {
    this.setState({ product_form_data: { original_price: e.target.value } });
  };
  onUpdateProfit = e => {
    this.setState({ product_form_data: { profit: e.target.value } });
  };
  onUpdateSellingPrice = e => {
    this.setState({ product_form_data: { selling_price: e.target.value } });
  };
  onUpdateSupplier = e => {
    this.setState({ product_form_data: { supplier: e.target.value } });
  };

  AddProduct = () => {
    const {
      product_name,
      quantity,
      measurement,
      original_price,
      profit,
      selling_price,
      supplier
    } = this.state.product_form_data;
    const product_details = {
      id: 2,
      product_name: product_name,
      quantity: quantity,
      measurement: measurement,
      original_price: original_price,
      profit: profit,
      selling_price: selling_price,
      supplier: supplier
    };
    const id = this.state.items.length + 1;
    product_details.id = id;
    this.setState({
      items: this.state.items.concat([product_details])
    });
    console.log(this.state.product_form_data);
  };

  OpenAddProductWindow = () => {
    this.state.window_value === "true"
      ? this.setState({ window_value: "false" })
      : this.setState({ window_value: "true" });
  };

  render() {
    return (
      <div className="main-container">
        <div className="header" id="item-1">
          <h1>Simple Inventory System</h1>
        </div>
        <table className="items-container">
          <tbody>
            <tr>
              <th>#</th>
              <th>Product Name</th>
              <th>Quantity</th>
              <th>Measurement</th>
              <th>Original Price</th>
              <th>Profit</th>
              <th>Selling Price</th>
              <th>Supplier</th>
              <th>Delete</th>
              <th>Edit</th>
            </tr>
            {this.state.items.map(items => (
              <Items items={items} key={items.id} />
            ))}
          </tbody>
        </table>
        <div>
          <div visible={this.state.window_value}>
            <AddProduct
              AddProduct={this.AddProduct}
              product_details={this.state.product_details}
              onUpdateProductName={this.onUpdateProductName}
              onUpdateQuantity={this.onUpdateQuantity}
              onUpdateMeasurement={this.onUpdateMeasurement}
              onUpdateOriginalPrice={this.onUpdateOriginalPrice}
              onUpdateProfit={this.onUpdateProfit}
              onUpdateSellingPrice={this.onUpdateSellingPrice}
              onUpdateSupplier={this.onUpdateSupplier}
            />
          </div>
          <div onClick={this.OpenAddProductWindow}>Add Product</div>
        </div>
      </div>
    );
  }
}

export default App;
