import React, { Component } from "react";
import "../css/fileUpload.css";
class FileUpload extends Component {
  render() {
    const { isChange, imageData } = this.props;

    return (
      <div>
        <input type="file" name="file" onChange={e => isChange(e)} />
        <img src={imageData} class="item-image" />
      </div>
    );
  }
}

export default FileUpload;
