import React, { Component } from "react";
import "./App.css";
import Items from "./components/items";
import AddProduct from "./components/addproduct";

class App extends Component {
  state = {
    window_value: "true",
    items: [
      {
        id: 1,
        product_name: "Pen",
        quantity: 35,
        measurement: "Piece",
        original_price: 25,
        profit: 10,
        selling_price: 35,
        supplier: "Smart Supplier"
      }
    ]
  };

  AddProduct = () => {
    console.log("test");
    const product_details = {
      id: 2,
      product_name: "Milo",
      quantity: 25,
      measurement: "Piece",
      original_price: 5,
      profit: 5,
      selling_price: 10,
      supplier: "Test Supplier"
    };
    this.setState({ items: this.state.items.concat([product_details]) });
  };

  OpenAddProductWindow = () => {
    return true;
  };

  render() {
    return (
      <div className="main-container">
        <div className="header" id="item-1">
          <h1>Simple Inventory System</h1>
        </div>
        <table className="items-container">
          <tbody>
            <tr>
              <th>#</th>
              <th>Product Name</th>
              <th>Quantity</th>
              <th>Measurement</th>
              <th>Original Price</th>
              <th>Profit</th>
              <th>Selling Price</th>
              <th>Supplier</th>
              <th>Delete</th>
              <th>Edit</th>
            </tr>
            {this.state.items.map(items => (
              <Items items={items} key={items.id} />
            ))}
          </tbody>
        </table>
        <div>
          <div visible={this.state.window_value}>
            <AddProduct />
          </div>
          <div onClick={this.OpenAddProductWindow}>Add Product</div>
        </div>
      </div>
    );
  }
}

export default App;
