import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: ""
    };
  }

  onChange(e) {
    let files = e.target.files;
    let reader = new FileReader();
    console.log(files);
    //reader.readAsDataURL(files[0]);

    reader.onload = e => {
      const imageData = e.target.result;
      this.setState({ image: imageData });
    };
  }

  render() {
    return (
      <div class="main-container">
        <div class="header" id="item-1">
          <h1>Simple Casher Counter</h1>
        </div>
        <div class="casher-contents">
          <div class="casher-buttons">
            <div>
              <button>7</button>
              <button>8</button>
              <button>9</button>
            </div>
            <div>
              <button>4</button>
              <button>5</button>
              <button>6</button>
            </div>
            <div>
              <button>1</button>
              <button>2</button>
              <button>3</button>
            </div>
            <div>
              <button>0</button>
              <button class="enter-button">Enter</button>
            </div>
          </div>
          <div class="items-list">
            <h2>Items List</h2>
            <input type="file" name="file" onChange={e => this.onChange(e)} />
            <img src={this.state.image} class="item-image" />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
