import React, { Component } from "react";

class FileUpload extends Component {
  state = {};
  render() {
    return (
      <div>
        <input type="file" name="file" onChange={e => this.onChange(e)} />
        <img src={this.state.image} class="item-image" />
      </div>
    );
  }
}

export default FileUpload;
